(11820, 95, '_cs_content_blocks_1_cs_case_study_block_0_cs_case_study_structure_3_cs_waggle_heading_1', 'field_57e37de254f27'),
(11821, 95, 'cs_content_blocks_1_cs_case_study_block_0_cs_case_study_structure_3_cs_waggle_content_1', 'Knewton technology analyzes data to figure out what students know and what they need to work on, providing them with personalized assignments to help them meet course goals, master material, and get ahead.\r\n\r\n<em>Because this differentiation happens automatically, teachers can spend their time helping and inspiring their students.</em>'),
(11822, 95, '_cs_content_blocks_1_cs_case_study_block_0_cs_case_study_structure_3_cs_waggle_content_1', 'field_57e37df654f28'),
